import React, { Component } from "react";
import Layout from "../components/Layout";
import {
  Table,
  Icon,
  Button,
  Form,
  Grid,
  Header,
  Message,
  Segment,
  Input,
  Container
} from "semantic-ui-react";
import { post } from "axios";

class mailInform extends Component {

  exit = () => {
    window.close();
  }


  render() {
  this.props.history.block();
  console.log(this.props.location.state);

    return (
          <Container style={{width:"800px", padding:40}}>
            <Table basic style={{ width: "500px" }}>
            <Table.Header />
              <Table.Body>
                <Table.Row>
                  <Table.Cell textAlign="center">이전 터미널</Table.Cell>
                  <Table.Cell textAlign="center">{this.props.location.state.data.hub}</Table.Cell>
                  <Table.Cell textAlign="center">{this.props.location.state.data.hub_time}</Table.Cell>
                </Table.Row>
                <Table.Row>
                  <Table.Cell textAlign="center">다음 터미널</Table.Cell>
                </Table.Row>
                <Table.Row>
                  <Table.Cell textAlign="center">배송 주소</Table.Cell>
                  <Table.Cell textAlign="center">{this.props.location.state.data.receiverAddress}</Table.Cell>
                </Table.Row>
              </Table.Body>
            </Table>
          </Container>
        );
}
}

export default mailInform;
