import React, { Component } from "react";
import { post } from "axios";

class Pass extends Component {
  getMail = () => {
    const url = "/api/getonemail";
    const formData = new FormData();
    formData.append("address", this.props.match.params.address);
    formData.append("index", this.props.match.params.index);
    const config = {
      headers: {
        "content-type": "multipart/form-data"
      }
    };
    post(url, formData, config).then(res => {
      this.props.history.push("/mail/"+this.props.match.params.address+"/mailInform");
      //console.log(this.props.history);
    });
  };
  render(){
    return(this.getMail());
  }
}

export default Pass;
