import React, { Component } from "react";
import { Button, Dimmer, Loader } from "semantic-ui-react";
import { post } from "axios";
import Web3 from "web3";
import HDWalletProvider from "truffle-hdwallet-provider";

class Pass extends Component {
  getAccounts = callback => {
    const provider = new HDWalletProvider( //connect to target network and unlock account
      "speed tell turtle spring try direct maid portion final erase attitude release",
      "https://rinkeby.infura.io/v3/0e555da2ac9e47f596b78b40a8f57ba7"
    );
    const web3 = new Web3(provider);
    web3.eth.getAccounts((error, result) => {
      if (error) {
        console.log(error);
      } else {
        callback(result);
      }
    });
  };

  getAdminMail = () => {
    const url = "/api/getonemail";
    const formData = new FormData();
    formData.append("address", this.props.match.params.address);
    formData.append("index", this.props.match.params.index);
    const config = {
      headers: {
        "content-type": "multipart/form-data"
      }
    };
    post(url, formData, config).then(res => {
      const data = res.data;
      this.props.history.push(
        "/mail/" + this.props.match.params.address + "/mailInform",
        { data }
      );
      //console.log(this.props.history);
    });
  };

  getDeliveryMail = () => {
    const url = "/api/getonemail";
    const formData = new FormData();
    formData.append("address", this.props.match.params.address);
    formData.append("index", this.props.match.params.index);
    const config = {
      headers: {
        "content-type": "multipart/form-data"
      }
    };
    post(url, formData, config).then(res => {
      const data = res.data;
      const index = this.props.match.params.index;
      this.props.history.push(
        "/mail/" + this.props.match.params.address + "/mailDeliveryInform",
        { data, index }
      );
      //console.log(this.props.history);
    });
  };

  getMail = () => {
    const url = "/api/getusermail";
    const formData = new FormData();
    formData.append("address", this.props.match.params.address);
    formData.append("index", this.props.match.params.index);
    const config = {
      headers: {
        "content-type": "multipart/form-data"
      }
    };
    post(url, formData, config).then(res => {
      const data = res.data;
      console.log(data);
      this.props.history.push(
        "/mail/" + this.props.match.params.address + "/mailUserInform",
        { data }
      );
      //console.log(this.props.history);
    });
  };

  constructor(props) {
    super(props);
    this.getAccounts(function(result) {
      console.log(result[0]);
    });
    window.addEventListener("load", () => {
      if (typeof web3 !== "undefined") {
        this.getAdminMail();
      } else {
        this.getMail();
      }
    });
  }

  render() {
    return (
      <Dimmer active inverted>
        <Loader inverted>Loading</Loader>
      </Dimmer>
    );
  }
}

export default Pass;
